class BentoGrid { constructor() { this.bentoGrid = document.querySelector('.bento-grid'); this.videoCard = document.querySelector('.bento-card-video'); this.iframe = document.querySelector('.video-container iframe'); this.eventCards = document.querySelectorAll('.event-details-card'); this.isVideoLoaded = false; this.init(); } init() { if (!this.bentoGrid) { console.warn('Bento Grid: Container not found'); return false; } this.setupLazyVideoLoading(); this.setupAccessibility(); this.setupResponsiveInteractions(); this.setupIntersectionObserver(); return true; } setupLazyVideoLoading() { if (!this.iframe) return; const videoObserver = new IntersectionObserver((entries) => { entries.forEach(entry => { if (entry.isIntersecting && !this.isVideoLoaded) { this.loadVideo(); videoObserver.unobserve(entry.target); } }); }, { rootMargin: '50px 0px', threshold: 0.1 }); if (this.videoCard) { videoObserver.observe(this.videoCard); } } loadVideo() { if (!this.iframe || this.isVideoLoaded) return; if (!this.iframe.hasAttribute('loading')) { this.iframe.setAttribute('loading', 'lazy'); } this.iframe.setAttribute('title', 'OECD Career Fair Value Video'); this.isVideoLoaded = true; console.log('Bento Grid: Video loaded successfully'); } setupAccessibility() { const allCards = document.querySelectorAll('.bento-card'); allCards.forEach((card, index) => { if (card.querySelector('iframe') || card.querySelector('button') || card.querySelector('a')) { card.setAttribute('tabindex', '0'); card.addEventListener('keydown', (e) => { if (e.key === 'Enter' || e.key === ' ') { const interactive = card.querySelector('iframe, button, a'); if (interactive) { e.preventDefault(); interactive.focus(); } } }); } if (card.classList.contains('event-details-card')) { card.setAttribute('aria-label', 'Event details and information card'); } else if (card.classList.contains('bento-card-video')) { card.setAttribute('aria-label', 'Educational video about career fairs'); } }); const icons = document.querySelectorAll('.event-detail-icon svg'); icons.forEach(icon => { icon.setAttribute('role', 'img'); icon.setAttribute('focusable', 'false'); }); } setupResponsiveInteractions() { if ('ontouchstart' in window) { const cards = document.querySelectorAll('.bento-card'); cards.forEach(card => { card.addEventListener('touchstart', () => { card.style.transform = 'scale(0.98)'; }, { passive: true }); card.addEventListener('touchend', () => { card.style.transform = ''; }, { passive: true }); card.addEventListener('touchcancel', () => { card.style.transform = ''; }, { passive: true }); }); } this.handleResponsiveLayout(); let resizeTimeout; window.addEventListener('resize', () => { clearTimeout(resizeTimeout); resizeTimeout = setTimeout(() => { this.handleResponsiveLayout(); }, 150); }); } handleResponsiveLayout() { if (!this.bentoGrid) return; const screenWidth = window.innerWidth; if (screenWidth < 768) { this.bentoGrid.style.gridTemplateColumns = '1fr'; } else if (screenWidth < 850) { this.bentoGrid.style.gridTemplateColumns = '1fr'; } else { this.bentoGrid.style.gridTemplateColumns = '1fr 1fr'; } } setupIntersectionObserver() { const animationObserver = new IntersectionObserver((entries) => { entries.forEach(entry => { if (entry.isIntersecting) { entry.target.classList.add('animate-in'); const delay = Array.from(this.bentoGrid.children).indexOf(entry.target) * 100; setTimeout(() => { entry.target.style.animationDelay = `${delay}ms`; }, delay); } }); }, { threshold: 0.1, rootMargin: '0px 0px -50px 0px' }); const cards = document.querySelectorAll('.bento-card'); cards.forEach(card => { animationObserver.observe(card); }); } refreshVideo() { if (this.iframe) { const src = this.iframe.src; this.iframe.src = ''; setTimeout(() => { this.iframe.src = src; }, 100); } } getState() { return { isInitialized: !!this.bentoGrid, isVideoLoaded: this.isVideoLoaded, cardCount: this.eventCards.length, hasVideo: !!this.iframe, currentLayout: window.innerWidth < 768 ? 'mobile' : window.innerWidth < 850 ? 'tablet-narrow' : 'desktop' }; } enhanceVideoAccessibility(title, description) { if (this.iframe) { if (title) { this.iframe.setAttribute('title', title); } if (description) { this.iframe.setAttribute('aria-describedby', description); } } } addEventHandler(selector, event, handler) { const elements = this.bentoGrid.querySelectorAll(selector); elements.forEach(element => { element.addEventListener(event, handler); }); } destroy() { if (this.animationObserver) { this.animationObserver.disconnect(); } if (this.videoObserver) { this.videoObserver.disconnect(); } if (this.resizeTimeout) { clearTimeout(this.resizeTimeout); } this.bentoGrid = null; this.videoCard = null; this.iframe = null; this.eventCards = null; } } if (typeof module !== 'undefined' && module.exports) { module.exports = BentoGrid; } else { window.BentoGrid = BentoGrid; }