class Footer { constructor() { this.footer = document.querySelector('.footer-bar'); this.footerContent = document.querySelector('.footer-content'); this.footerLinks = document.querySelectorAll('.footer-link'); this.lastScrollY = window.scrollY; this.isHidden = false; this.init(); } init() { if (!this.footer) { console.warn('Footer: Footer element not found'); return false; } this.setupLinkTracking(); this.setupResponsiveBehavior(); this.setupAccessibility(); this.setupEmailLink(); return true; } setupLinkTracking() { this.footerLinks.forEach(link => { link.addEventListener('click', (e) => { const linkText = link.textContent.trim(); const linkHref = link.href; this.trackLinkClick(linkText, linkHref); if (linkHref.startsWith('mailto:')) { this.handleEmailClick(e, linkHref); } if (link.hasAttribute('target') && link.getAttribute('target') === '_blank') { this.handleExternalLink(e, linkHref); } }); link.addEventListener('keydown', (e) => { if (e.key === 'Enter' || e.key === ' ') { e.preventDefault(); link.click(); } }); }); } setupResponsiveBehavior() { let resizeTimeout; window.addEventListener('resize', () => { clearTimeout(resizeTimeout); resizeTimeout = setTimeout(() => { this.adjustFooterLayout(); }, 150); }); this.adjustFooterLayout(); if (window.innerWidth <= 767) { this.setupMobileScrollBehavior(); } } adjustFooterLayout() { if (!this.footerContent) return; const screenWidth = window.innerWidth; if (screenWidth <= 480) { this.footerContent.style.flexDirection = 'column'; this.footerContent.style.gap = '4px'; this.footer.style.padding = '8px 0'; } else if (screenWidth <= 767) { this.footerContent.style.flexDirection = 'row'; this.footerContent.style.flexWrap = 'wrap'; this.footerContent.style.gap = '2px'; this.footer.style.padding = '12px 0'; } else { this.footerContent.style.flexDirection = 'row'; this.footerContent.style.flexWrap = 'nowrap'; this.footerContent.style.gap = 'normal'; this.footer.style.padding = '12px 0'; } } setupMobileScrollBehavior() { let scrollTimeout; window.addEventListener('scroll', () => { clearTimeout(scrollTimeout); scrollTimeout = setTimeout(() => { const currentScrollY = window.scrollY; const scrollDifference = currentScrollY - this.lastScrollY; if (Math.abs(scrollDifference) > 10) { if (scrollDifference > 0 && currentScrollY > 100) { this.hideFooter(); } else { this.showFooter(); } } this.lastScrollY = currentScrollY; }, 100); }, { passive: true }); } setupAccessibility() { this.footerLinks.forEach((link, index) => { if (link.href.startsWith('mailto:')) { link.setAttribute('aria-label', `Send email to ${link.textContent.trim()}`); } else if (link.href.includes('myblueprint.ca')) { link.setAttribute('aria-label', 'Visit myBlueprint website (opens in new tab)'); } link.setAttribute('tabindex', '0'); }); this.footer.setAttribute('role', 'contentinfo'); this.footer.setAttribute('aria-label', 'Site footer with contact information'); } setupEmailLink() { const emailLink = document.querySelector('a[href^="mailto:"]'); if (emailLink) { emailLink.addEventListener('click', (e) => { this.trackEmailInteraction(emailLink.href); emailLink.style.transform = 'scale(0.95)'; setTimeout(() => { emailLink.style.transform = ''; }, 150); }); } } hideFooter() { if (!this.isHidden) { this.footer.style.transform = 'translateY(100%)'; this.footer.style.transition = 'transform 0.3s ease-in-out'; this.isHidden = true; } } showFooter() { if (this.isHidden) { this.footer.style.transform = 'translateY(0)'; this.footer.style.transition = 'transform 0.3s ease-in-out'; this.isHidden = false; } } trackLinkClick(linkText, linkHref) { try { const trackingData = { event: 'footer_link_click', link_text: linkText, link_url: linkHref, timestamp: new Date().toISOString(), page: 'career_launch_landing' }; console.log('Footer: Link click tracked:', trackingData); } catch (error) { console.warn('Footer: Link tracking failed:', error); } } trackEmailInteraction(emailHref) { try { const trackingData = { event: 'email_contact_attempt', email: emailHref.replace('mailto:', ''), timestamp: new Date().toISOString(), source: 'footer' }; console.log('Footer: Email interaction tracked:', trackingData); } catch (error) { console.warn('Footer: Email tracking failed:', error); } } handleEmailClick(event, emailHref) { const email = emailHref.replace('mailto:', ''); if (/Mobi|Android/i.test(navigator.userAgent)) { console.log('Footer: Opening email client on mobile device'); } else { console.log('Footer: Opening email client on desktop'); } } handleExternalLink(event, linkHref) { this.trackExternalLink(linkHref); if (linkHref.includes('myblueprint.ca')) { console.log('Footer: Navigating to myBlueprint website'); } } trackExternalLink(linkHref) { try { const trackingData = { event: 'external_link_click', destination: linkHref, timestamp: new Date().toISOString(), source: 'footer' }; console.log('Footer: External link tracked:', trackingData); } catch (error) { console.warn('Footer: External link tracking failed:', error); } } getState() { return { isInitialized: !!this.footer, isHidden: this.isHidden, linkCount: this.footerLinks.length, isFixed: this.footer ? this.footer.style.position === 'fixed' : false, currentLayout: window.innerWidth <= 480 ? 'stacked' : window.innerWidth <= 767 ? 'wrapped' : 'single-line' }; } forceShow() { this.showFooter(); } updateContent(newContent) { if (this.footerContent && newContent) { this.footerContent.innerHTML = newContent; this.footerLinks = document.querySelectorAll('.footer-link'); this.setupLinkTracking(); this.setupAccessibility(); } } destroy() { if (this.scrollTimeout) { clearTimeout(this.scrollTimeout); } if (this.resizeTimeout) { clearTimeout(this.resizeTimeout); } this.footer = null; this.footerContent = null; this.footerLinks = null; } } if (typeof module !== 'undefined' && module.exports) { module.exports = Footer; } else { window.Footer = Footer; }